INSERT INTO knowledge_base (title, content, category, product_type, metadata) VALUES
('ข้อมูลเกี่ยวกับอ่าง', 'TOA JOMOO มีสินค้าอ่างสองประเภทหลัก ได้แก่ อ่างล้างหน้า (Basin) สำหรับพื้นที่ล้างหน้าและแปรงฟัน และอ่างอาบน้ำ (Bathtub) สำหรับการแช่ตัวผ่อนคลาย ทั้งสองประเภทผลิตจากวัสดุคุณภาพสูง ทนทาน และมีดีไซน์ที่หลากหลาย', 'product_info', 'general', '{"features": ["อ่างล้างหน้า", "อ่างอาบน้ำ", "วัสดุคุณภาพ"], "page_links": ["/basin", "/bathtub"]}'),

('อ่างสำหรับห้องน้ำ', 'JOMOO มีอ่างหลากหลายรูปแบบ ทั้งอ่างล้างหน้าที่สามารถติดตั้งแบบวางบน ฝังใน หรือแขวนผนัง และอ่างอาบน้ำที่มีทั้งแบบฝังพื้น แบบยืนอิสระ และแบบเข้ามุม เหมาะสำหรับพื้นที่ห้องน้ำทุกขนาด', 'product_info', 'general', '{"installation_types": ["วางบน", "ฝังใน", "แขวนผนัง", "ฝังพื้น", "ยืนอิสระ"], "page_links": ["/basin", "/bathtub"]}'),

('การเลือกอ่างที่เหมาะสม', 'การเลือกอ่างควรพิจารณาจากขนาดพื้นที่ รูปแบบการใช้งาน และดีไซน์ที่ต้องการ สำหรับอ่างล้างหน้าควรเลือกขนาดที่เหมาะกับเคาน์เตอร์ ส่วนอ่างอาบน้ำควรเลือกตามพื้นที่ห้องน้ำและจำนวนผู้ใช้', 'product_info', 'general', '{"considerations": ["ขนาดพื้นที่", "รูปแบบการใช้งาน", "ดีไซน์"], "page_links": ["/basin", "/bathtub"]}');