INSERT INTO knowledge_base (title, content, category, product_type, metadata) VALUES
('แรงบันดาลใจการออกแบบห้องน้ำ', 'TOA JOMOO นำเสนอไอเดียการออกแบบห้องน้ำสไตล์ต่างๆ ตั้งแต่แบบโมเดิร์น มินิมอล คลาสสิก จนถึงลักชูรี่ พร้อมคำแนะนำการเลือกสินค้าและการจัดวางที่เหมาะสมสำหรับพื้นที่แต่ละขนาด', 'inspiration_info', 'general', '{"styles": ["โมเดิร์น", "มินิมอล", "คลาสสิก", "ลักชูรี่"], "page_link": "/inspiration"}'),

('ไอเดียการแต่งห้องน้ำ', 'รวมไอเดียการแต่งห้องน้ำจาก JOMOO ที่ผสมผสานฟังก์ชันการใช้งานกับความสวยงาม ไม่ว่าจะเป็นการเลือกสีสัน การจัดแสง การเลือกสุขภัณฑ์ และการใช้พื้นที่ให้มีประสิทธิภาพสูงสุด', 'inspiration_info', 'general', '{"topics": ["การเลือกสี", "การจัดแสง", "การใช้พื้นที่"], "page_link": "/inspiration"}'),

('เทรนด์การออกแบบห้องน้ำ', 'เทรนด์การออกแบบห้องน้ำล่าสุดที่ JOMOO แนะนำ รวมถึงการใช้วัสดุธรรมชาติ โทนสีที่นุ่มนวล เทคโนโลยี Smart Home และการออกแบบที่เป็นมิตรกับสิ่งแวดล้อม เพื่อสร้างพื้นที่ผ่อนคลายที่สมบูรณ์แบบ', 'inspiration_info', 'general', '{"trends": ["วัสดุธรรมชาติ", "Smart Home", "เป็นมิตรกับสิ่งแวดล้อม"], "page_link": "/inspiration"}');